select case o.order_type when '1' then o.order_amount else (select sum(aa.amount) from car_scrap_order_autoparts aa where aa.order_id=o.id) end as order_amount,
case o.client_type when '1' then '个人' else (select c1.customer_name from sys_client c ,sys_company c1 where c.companyid = c1.id and c.id=o.client_id) end as custormName, (select t.real_name from sys_user t where t.id=o.agent_userid ) saleName, ( SELECT t.contact_phone FROM sys_user t WHERE t.id = o.agent_userid ) salePhone, ( SELECT t.contact_phone FROM sys_user t WHERE t.id = o.operator ) adminPhone,
o.car_model_number as carModelNumberName, o.*, o.operator as operatorId, fa.factory_name as factoryName, fa.factory_phone as factoryPhone, sa.areas_name orderAreas, kd.car_scrap_name as CourierName from car_scrap_order o left join sys_areas sa on o.order_areas_id = sa.id left join car_scrap_factory fa on o.factory_id = fa.id left join tb_car_courier kd on o.express_company = kd.car_scrap_num where o.isremove=0 and o.id =#{id,jdbcType=VARCHAR}

select o.id, o.order_no orderNo,o.order_time orderTime,o.car_frame_number carFrameNumber,o.order_type orderType, ( SELECT a2.storage_path FROM car_scrap_order_attachment a1, tb_attachment a2 WHERE a1.order_id = o.id AND a1.attachment_id = a2.id AND a1.car_picture_type IS NOT NULL ORDER BY a1.car_picture_type LIMIT 0, 1 ) AS listPicture, COALESCE(o.sub_quote,'?') branchOffice,COALESCE(o.order_amount,'?') headOffice, case o.order_type when '1' then '整车' else '配件' end as orderTypeS, case o.order_type when '1' then o.car_number else (select CONCAT('数量',':',(select count(*) from car_scrap_order_autoparts t where t.order_id=o.id))) end as carNumberOrPartsNum, o.car_model_number carModelNumberName, case o.client_type when '1' then '个人' else (select c1.customer_name from sys_client c,sys_company c1 where c.companyid = c1.id and c.id=o.client_id) end as custormName, o.order_status orderStatus, o.take_car_contacts takeCarContacts, o.take_car_contact_number takeCarContactNumber from car_scrap_order o where o.isremove=0 and ((o.order_type='2' and o.order_status<>'4') or o.order_type='1')
and (o.order_no like CONCAT('%',#{vo.keyWord},'%') or o.report_no like CONCAT('%',#{vo.keyWord},'%') or o.car_number like CONCAT('%',#{vo.keyWord},'%') or o.car_frame_number like CONCAT('%',#{vo.keyWord},'%') or o.take_car_contacts like CONCAT('%',#{vo.keyWord},'%') or o.take_car_contact_number like CONCAT('%',#{vo.keyWord},'%') or o.car_model_number like CONCAT('%',#{vo.keyWord},'%'))
and o.order_areas_id in
${item}
and o.order_status in
${item1}
and o.order_type = #{vo.orderType}
order by o.operatortime desc
