SELECT
    ${chaiorjiu}
FROM
    `tieshangj_car_pieces`
WHERE
    car_piece_id = #{chejianId}
AND car_model_tiema = #{tiema}

        SELECT
	        sum(${chaiorjiu})
      FROM
          `tieshangj_car_pieces`
      WHERE
        car_piece_id != #{chejianId1}
        AND car_piece_id  not BETWEEN #{chejianId2} and #{chejianId3}
        and  car_model_tiema = #{tiema}

SELECT
    sum(${chaiorjiu})
FROM
    `tieshangj_car_pieces`
WHERE
    car_piece_id != #{chejianId1}
    AND car_piece_id != #{chejianId2}
    and  car_model_tiema = #{tiema}

SELECT
    sum(${chaiorjiu})
FROM
    `tieshangj_car_pieces`
WHERE
   car_piece_id NOT BETWEEN #{chejianId1}
    AND #{chejianId2}
    AND car_piece_id NOT BETWEEN #{chejianId3}
    AND #{chejianId4}
    and car_model_tiema = #{tiema}
