mapper.select:
SELECT SYSDATE() as sysDate, concat(sia.driving_brand," ",sia.driving_model) as carInfo , sia.full_name as fullName , sia.auction_type as auctionType, DATE_FORMAT(sia.register_date,'%Y-%m-%d') as registerDate, sias.starting_price as startingPrice, sias.price_increase as priceIncrease, sia.park_address as parkAddress, sia.transfer_date as transferDate, sias.auction_cash_deposit as auctionCashDeposit, sias.commission_type as commissionType, sias.commission as commission, sias.commission_is_discount as commissionIsDiscount, sias.commission_percent_type as commissionPercentType, sias.commission_discount_value as commissionDiscountValue, sias.rescue_cost as rescueCost, sias.other_cost as otherCost, sias.auction_time as auctionStartTime, sias.compete_over_time as auctionFinishTime, sias.interval_time as intervalTime, sias.order_state as state, sia.vehicle_name as vehicleName, sia.driving_vin as drivingVin, siw.car_card as carCard, siw.`key` as key1, siw.spare_key as spareKey, sia.strong_risk_date as strongRiskDate, sia.annual_survey_date as annualSurveyDate, concat(sia.displacement,'',sia.displacement_type) as displacement, sia.gearbox_type as gearboxType, sia.driving_type as drivingType , sia.car_remark as carRemark, siw.dismantling_part as dismantlingPart, sia.driving_no as drivingNo, sia.driving_no as drivingNo1, sia.license_number as licenseNumber , sias.id as id , sip.auction_name as pmhName, sip.auction_platform as auctionPlatform, (CASE WHEN spca.ID IS NULL THEN '0' ELSE spca.IS_UNFOLLOW END) as isUnfollow, ( SELECT MAX(HB.BID_AMOUNT) FROM SDIC_PM_OFFER_WATER HB WHERE HB.DELETE_TAG = '0' AND HB.PROMISES_TYPE = '2' AND HB.BID_TYPE IN ('0', '3') AND HB.ORDER_ID = sias.id) as highestPrice FROM sdic_inner_auction_set sias LEFT JOIN sdic_inner_auction sia ON sias.auction_id = sia.id LEFT JOIN sdic_inner_paimai sip ON sias.pmh_id = sip.pmh_id LEFT JOIN sdic_inner_warehouse siw ON sia.id = siw.auction_id LEFT JOIN sdic_pm_customer_attention spca ON spca.DELETE_TAG='0' AND spca.TIMELINESS='0' AND spca.MEMBER_CODE=#{memberCode} AND spca.ORDER_ID= sias.id WHERE sias.is_delete = 0 AND sia.is_delete = 0 AND sip.paimai_state = 1
AND sia.driving_type = #{drivingType}
AND sias.order_state = #{state}
AND sia.province = #{parkAddress}
AND sia.driving_brand = #{drivingBrand}
AND sip.auction_platform = '国投互联'
AND sip.auction_platform = '中拍协'
AND (sias.order_state = '10' OR sias.order_state = '20' OR sias.order_state = '30')
ORDER BY sias.order_state DESC,sias.pm_order

SELECT COUNT(1) FROM car_pm_auction_set sias LEFT JOIN car_pm_auction sia ON sias.auction_id = sia.id LEFT JOIN car_pm_paimai sip ON sias.pmh_id = sip.pmh_id WHERE sias.is_delete = 0 AND sia.is_delete = 0 AND sip.paimai_state = 1
AND sia.driving_type = #{drivingType}
AND sias.order_state = #{state}
AND sia.province = #{parkAddress}
AND sia.driving_brand = #{drivingBrand}
AND sip.auction_platform = '国投互联'
AND sip.auction_platform = '中拍协'
AND (sias.order_state = '10' OR sias.order_state = '20' OR sias.order_state = '30')
ORDER BY sias.order_state DESC,sias.pm_order

mapper.update:
UPDATE
	car_pm_auction_set
set
	order_state = #{orderState}
WHERE
	ID = #{orderId}
